import { createSlice } from "@reduxjs/toolkit";

const appSlice = createSlice({
  name: "app",
  initialState: {
    isOpen: true,
  },
  reducers: {
    toggleMenu: (state) => {
      state.isOpen = !state.isOpen;
    },
    closeMenu: (state) => {
        state.isOpen = false;
      },

  },
});

export default appSlice.reducer;
export const {toggleMenu,closeMenu} = appSlice.actions
