import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { toggleMenu } from "./utils/appSlice";

const Header = () => {
  const dispatch = useDispatch();
  const [searchData, setSearchData] = useState("");

  const toggleMenuHandler = () => {
    dispatch(toggleMenu());
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      fetchData();
    }, 2000);

    return () => {
      clearTimeout(timer);
    };
  }, [searchData]);

  const fetchData = async () => {
    const api = await fetch(
      `http://suggestqueries.google.com/complete/search?client=firefox&ds=yt&q=${searchData}`
    );
    const result = await api.json();
    console.log(result[1]);
  };

  return (
    <div className="grid grid-flow-col justify-between items-center m-2 p-2 bg-gray-100 border-b border-gray-300">
      <div className="flex col-span-1 items-center">
        <img
          onClick={toggleMenuHandler}
          className="cursor-pointer w-12 h-12"
          src="data:image/png;base64,"
          alt="menu"
        />
        <img
          className="w-24 h-22"
          src="data:image/png;base64,"
          alt="YouTube"
        />
      </div>
      <div className="col-span-10 px-10">
        <input
          className="w-1/2 px-4 py-2 border border-gray-400 rounded-l-full"
          type="text"
          value={searchData}
          onChange={(e) => setSearchData(e.target.value)}
        />
        <button className="px-4 py-2 border border-gray-400 rounded-r-full bg-gray-200">
          🔍
        </button>
      </div>
      <div className="col-span-1">
        <img
          className="w-8 h-8"
          src="https://www.iconfinder.com/data/icons/social-media-2210/24/YouTube-512.png"
          alt="User Icon"
        />
      </div>
    </div>
  );
};

export default Header;
