import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toggleMenu } from "./utils/appSlice";
import { cacheResults, newSearchQuery } from "./utils/searchSlice";

const Header = () => {
  const dispatch = useDispatch();
  const [searchData, setSearchData] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const searchCache = useSelector((store) => store.search);
  const isClicked = useSelector((store) => store.app.isClicked);

  const toggleMenuHandler = () => {
    dispatch(toggleMenu());
  };

  useEffect(() => {
    if (searchData.trim() === "") {
      setSuggestions([]);
      return;
    }

    const timer = setTimeout(() => {
      if (searchCache[searchData]) {
        setSuggestions(searchCache[searchData]);
      } else {
        fetchData();
      }
    }, 2000);

    return () => {
      clearTimeout(timer);
    };
  }, [searchData]);

  const fetchData = async () => {
    try {
      const api = await fetch(
        "http://suggestqueries.google.com/complete/search?client=firefox&ds=yt&q=" +
          searchData
      );
      const result = await api.json();
      setSuggestions(result[1]);
      dispatch(cacheResults({ [searchData]: result[1] }));
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchData(suggestion);
    setShowSuggestions(false);
    dispatch(newSearchQuery(suggestion));
  };

  const handleBlur = () => {
    setTimeout(() => setShowSuggestions(false), 100);
  };

  return (
    <div className="fixed top-0 left-0 w-full h-22 bg-white text-white px-4 py-3 z-50">
      <div className="grid grid-flow-col gap-2 mx-2 px-2 overflow-hidden">
        <div className="flex col-span-1 items-center">
          <img
            onClick={toggleMenuHandler}
            className="cursor-pointer w-12 h-12"
            src="data:image/png;base64,..."
            alt="menu"
          />
          <img
            className="w-24 h-22"
            src="data:image/png;base64,..."
            alt="logo"
          />
        </div>

        <div className="flex flex-col col-span-10 items-center">
          <div className="w-6/12">
            <div className="flex justify-center w-full">
              <input
                className="border rounded-l-full w-full h-12 px-8"
                type="text"
                name="search"
                value={searchData}
                onChange={(e) => setSearchData(e.target.value)}
                onFocus={() => setShowSuggestions(true)}
                onBlur={handleBlur}
              />
            </div>
            {showSuggestions && suggestions.length > 0 && (
              <ul className="border border-gray-300 mt-2 bg-white absolute w-6/12">
                {suggestions.map((suggestion, index) => (
                  <li
                    key={index}
                    className="px-4 py-2 cursor-pointer hover:bg-gray-200"
                    onClick={() => handleSuggestionClick(suggestion)}
                  >
                    {suggestion}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
