import React, { useEffect, useState } from 'react';
import { video_API } from './utils/constants';
import Videos from './Videos';
import { Link } from 'react-router-dom';

const VideoContainer = () => {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    getVideos();
  }, []);

  const getVideos = async () => {
    try {
      const api = await fetch(video_API);
      const { items } = await api.json();
      if (items) {
        setVideos(items);
      } else {
        setVideos([]); // Set an empty array if items is not defined or null
      }
    } catch (error) {
      console.error('Error fetching videos:', error);
      setVideos([]); // Set an empty array on error to prevent undefined issues
    }
  };

  return (
    <div className='w-12/12 grid md:grid-cols-4 sm:grid-cols-1'>
      {videos && videos.length > 0 ? (
        videos.map((data) => (
          <Link key={data.id.videoId || data.id.channelId} to={"/watch?v=" + (data.id.videoId || data.id.channelId)}>
            <Videos info={data} />
          </Link>
        ))
      ) : (
        <p>No videos found</p>
      )}
    </div>
  );
};

export default VideoContainer;
