import React from 'react'

const SearchedResultVideos = ({data}) => {
  const {snippet} = data;
  const {channelTitle,title,description} = snippet
  const {high} = snippet.thumbnails;
  const channelId = searchData.items[0].snippet.channelId; // Adjust the index if needed

// Fetch channel details using the channelId
const getChannelDetails = async (channelId) => {
  try {
    const channelResponse = await fetch(`https://youtube.googleapis.com/youtube/v3/channels?part=snippet&id=${channelId}&key=${apiKey}`);
    const channelData = await channelResponse.json();

    if (channelData.items.length > 0) {
      const channelImageUrl = channelData.items[0].snippet.thumbnails.default.url; // You can choose default, medium, or high quality
      console.log('Channel Image URL:', channelImageUrl);
      return channelImageUrl;
    } else {
      throw new Error('Channel not found');
    }
  } catch (error) {
    console.error('Error fetching channel details:', error);
  }
};

// Call the function to get the channel details
getChannelDetails(channelId);
  //console.log(data);
  return (
    <div className="flex w-auto my-2">
      <div className="ml-48 mr-2 h-72">
        <img
          className="rounded-xl w-fit h-64 "
          src={high.url}
          alt=""
        />
      </div>
      <div className="">
        <div>
          <h1 className="text-xl pt-1 font-medium">{title}</h1>
        </div>
        <div className="flex font-normal text-sm">
          <p className="mr-2">10k views</p>
          <p>10 days ago</p>
        </div>
        <div className="flex items-center mt-2 mb-3">
          <img 
            className="w-8 h-8 rounded-full mr-2"
            src="https://i.ytimg.com/vi/tgBTspqA5nY/hqdefault_live.jpg"
            alt=""
          />
          <h1>{channelTitle}</h1>
        </div>
        <div>
          <p className="font-normal text-sm">
           {description}
          </p>
        </div>
      </div>
    </div>

  )
}

export default SearchedResultVideos
